INSERT INTO TBL_MAILBOX(BNO, TEMPLATE, TITLE, CONTENT)
VALUES(#{bno, jdbcType=INTEGER}, #{template, jdbcType=VARCHAR}, #{title, jdbcType=VARCHAR}, #{content,  jdbcType=VARCHAR}
 
