mapper.select:

	SELECT BNO, TITLE1, TEXT1, REGDATE, TEMPLATE
	FROM
	(
	SELECT /*+INDEX_DESC(TBL_MAILBOX IDX_MAILBOX)*/
	ROWNUM RN, BNO, TITLE1, TEXT1, REGDATE, TEMPLATE
	FROM
		TBL_MAILBOX
	WHERE MNO = #{mno}
	AND BNO > 0
	AND	ROWNUM <= #{cri.pageNum} * #{cri.amount}
	)
	WHERE RN > (#{cri.pageNum} -1) * #{cri.amount}
